import * as d3 from "d3";
import moment from 'moment';

export function drawTimechart(data, options) {

    console.log('draw timechart');
    console.log(data);
    console.log(options);

    // define date range
    const minDate = moment(new Date(2020, 0, 1));
    const maxDate = moment(new Date(d3.max(data.who_data, function(d){
        return d.date;
    })));

    const numIntervals = d3.max(data.who_data, function(d,i){
        return d[options.timeline_frequency+'Total'];
    });

    const svg = d3.select('#timechart_svg').attr('fill', 'blue');
    const width = parseInt(svg.attr('width'));
    const barWidth = width/numIntervals;

    const xScale = d3.scaleLinear()
    .range([0,width])
    .domain([0,numIntervals]);

    console.log({'x0': xScale(0)})
    console.log({'x70': xScale(73)})
    console.log({'width': width});
    console.log({'barWidth': barWidth});
    console.log(minDate);
    console.log(maxDate);
    console.log({numIntervals});

    // var nestedData = d3.nest()


}

export function updateTimechart(data) {

    console.log('update timechart');

}

export default drawTimechart;
