      .style('font-size', '9px');

      // col 1 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow table_country_name')
      .text(function(){
        var val = parseFloat(d[1][0].total_vaccinations_per_hundred).toFixed(1);
        if(isNaN(val)){ return ''} else { return val }
      })
