import React, { useEffect } from "react";
import * as d3 from "d3";
import Layout from './Layout';
import options from './Options';

const d = {'hello': 123};
console.log(options);

function App() {

  async function init(){
    const layout = d3.xml("./images/layout.svg");
    layout.then(function(data){
      console.log(layout);
      d3.select('#container').node().append(d3.select(data.documentElement).attr('width', '100%').attr('height', 'auto').node());
      const svg = d3.select('#container svg');
      console.log('load layout');
      console.log(d);
      Layout(svg,d);
      console.log(d);
    })

  }

  useEffect(() => {
    init();
  }, [])

  return (
    <div id="container" className="App">

    </div>
  );
}

export default App;
