import * as d3 from "d3";
import moment from 'moment';
import colors from './colors';
import {addCommas} from './HelperFunctions';
import {filter} from './App';

let maxCases;
let maxDeaths;
let maxVaccinations;
let xScaleCases; 
let xScaleDeaths;
let xScaleVaccines;
let labelWidth; 
const regions = ['AF', 'ME', 'AP', 'EU', 'AM', 'OTH'];
const margin = { left: 20, top: 0, right: 58 };
let rcSize; 
let groupedData = [];

export function drawRegionchart(data, options, filter) {

    groupedData = [];

    const barSpacing = 0.1;

    regions.forEach(function(d,i){
        if(d!='OTH') // remove OTH
        groupedData.push({ region: d, cumulative_cases: 0, cumulative_deaths: 0, cumulative_vaccines: 0});
    })

    data.who_data.forEach(function(d,i){
        regions.forEach(function(dd,ii){
            if((dd==d.ifrc_region)&&(dd!='OTH')){ // remove OTH
                groupedData[ii].cumulative_cases += d.new_cases;
                groupedData[ii].cumulative_deaths += d.new_deaths;
            }
        })
    });

    maxCases = d3.max(groupedData, function(d,i){
        return d.cumulative_cases;
    })

    maxDeaths = d3.max(groupedData, function(d,i){
        return d.cumulative_deaths;
    })

    // vaccinations max date records
    var vGroup = d3.flatGroup(data.vaccines_data_cumulative, d => d.date);

    var vacGrouped = vGroup[vGroup.length-1];

