    vacGrouped[1].forEach(function(d,i){
        regions.forEach(function(dd,ii){
            if((dd==d.ifrc_region)&&(dd!='OTH')){ // remove OTH
                groupedData[ii].cumulative_vaccines += d.total_vaccinations;
            }
        })
    });

    groupedData.sort(function(a,b){
        if(options.region_chart_toggle == 'cases') return (b.cumulative_cases) - (a.cumulative_cases);
        if(options.region_chart_toggle == 'deaths') return (b.cumulative_deaths) - (a.cumulative_deaths);
        if(options.region_chart_toggle == 'vaccines') return (b.cumulative_vaccines) - (a.cumulative_vaccines); 
    });

    maxVaccinations = d3.max(groupedData, function(d,i){
        return d.cumulative_vaccines;
    })

    // append region chart containerg
    const regionChartContainer = d3.select('#layout #region_chart_container').attr('opacity', 0);
    rcSize = {
        x: regionChartContainer.attr('x'), y: regionChartContainer.attr('y'), width: regionChartContainer.attr('width'), height: regionChartContainer.attr('height')
    }

    const svg = d3.select('#layout').append('g').attr('transform', 'translate('+rcSize.x+', '+rcSize.y+')')
    .attr('id', 'region_chart');

	var rowHeight = rcSize.height/groupedData.length-1; // remove OTH

    var rows = svg.selectAll('.bar-row')
	.data(groupedData)
	.enter()
	.append('g')
	.attr('class', 'bar-row')
	.attr('transform', function(d,i){
		return 'translate(0,' + ((i*rowHeight) + margin.top) + ')';
	});

    var label = rows.append('text')
	.attr('y', rowHeight/2 )
    .attr('x', margin.left-5)
	.attr('class', function(d,i){ return 'bar-labels bar-label-'+d.region })
	.style('alignment-baseline', 'middle')
	.style('font-size', '11px')
    .attr('opacity', 1)
	.attr('fill', '#696969')
	.text(function(d,i){
		if(d.region==='AM') return 'Americas';
		if(d.region==='EU') return 'Europe';
		if(d.region==='AF') return 'Africa';
		if(d.region==='ME') return 'Middle East and North Africa';
		if(d.region==='AP') return 'Asia-Pacific';
	}).style('text-anchor', 'end');

    labelWidth = rows.node().getBBox().width + margin.left;
	label.attr('x', labelWidth+89);
	labelWidth = labelWidth + 94;
    d3.selectAll('.timeBar').attr('opacity',1);

    var rowBg = rows.append('rect')
	.attr('y', 0)
	.attr('x', 0)
	.attr('width', labelWidth)
	.attr('height', rowHeight-2)
	.style('opacity', 0)
    .attr('fill', 'grey')
	.style('cursor', 'pointer')
	.attr('class', function(d,i){ return 'bar-bg-'+d.region })
	.attr('data-id', function(d,i){ return d.region })
	.on('mouseover', function(d){
		d3.select(this).style('opacity', 0.1);
        // var dat = d3.select(this.parentNode).datum();
        // d3.selectAll('.timeBar:not(.time_'+dat.region).transition().duration(500).attr('opacity', 0.3);
        // d3.selectAll('.time_'+dat.region).attr('opacity', 1);
	})
	.on('mouseout', function(){
		d3.select(this).style('opacity', 0);
        // d3.selectAll('.timeBar').transition().duration(500).attr('opacity', 1);
	})
	.on('click', function(d,i){
        var t = d3.select(this).attr('data-id');
        filter([t],'regionchart');
    });

    xScaleCases = d3.scaleLinear()
	.range([0, rcSize.width - margin.right - labelWidth])
	.domain([0, maxCases]);// severity/reliability x xcale

    xScaleDeaths = d3.scaleLinear()
	.range([0, rcSize.width - margin.right - labelWidth])
	.domain([0, maxDeaths]);// severity/reliability x xcale

    xScaleVaccines = d3.scaleLinear()
	.range([labelWidth, rcSize.width - margin.right - labelWidth])
	.domain([0, maxVaccinations]);// severity/reliability x xcale

    var bars = rows.append('rect')
    .attr('id', function(d,i){
        return 'bar-'+d.region;
    })
	.attr('data-id', function(d,i){ return d.region })
    .attr('class', 'region-bar')
    .attr('width', function(d,i){
        if(options.region_chart_toggle == 'cases') return xScaleCases(d.cumulative_cases);
        if(options.region_chart_toggle == 'deaths') return xScaleDeaths(d.cumulative_deaths);
        if(options.region_chart_toggle == 'vaccines') return xScaleDeaths(d.cumulative_vaccines);
    })
    .attr('x', labelWidth)
    .style('outline', 'none')
    .style('cursor', 'pointer')
    .attr('y', rowHeight*barSpacing - 1)
    .attr('height', rowHeight-((rowHeight*barSpacing)*2))
    .style('fill', function(d){
        return colors.regions[d.region]
    })
    .attr('opacity',1)
    .on('click', function(d,i){
        var t = d3.select(this).attr('data-id');
        filter([t],'regionchart');
    })
    .on('mouseover', function(d){
        var dat = d3.select(this.parentNode).datum();
        // d3.selectAll('.timeBar:not(.time_'+dat.region).transition().duration(500).attr('opacity', 0.3);
        // d3.selectAll('.time_'+dat.region).attr('opacity', 1);
	})
	.on('mouseout', function(){
        // d3.selectAll('.timeBar').transition().duration(500).attr('opacity', 1);
	})

    var dataLabel = rows.append('text')
	.text(function(d){
        if(options.region_chart_toggle == 'cases') return addCommas(d.cumulative_cases);
        if(options.region_chart_toggle == 'deaths') return addCommas(d.cumulative_deaths);
        if(options.region_chart_toggle == 'vaccines') return addCommas(d.cumulative_vaccines);
    })
	.attr('id', function(d,i){
		return 'data-label'+d.region;
	})
    .attr('data-id', function(d,i){ return d.region })
	.attr('class', 'data-label')
	.attr('y', rowHeight/2)
	.style('alignment-baseline', 'middle')
	.style('text-anchor', 'start')
	.attr('x', function(d,i){
        if(options.region_chart_toggle == 'cases') return xScaleCases(d.cumulative_cases)+labelWidth+5;
        if(options.region_chart_toggle == 'deaths') return xScaleDeaths(d.cumulative_deaths)+labelWidth+5;
        if(options.region_chart_toggle == 'vaccines') return xScaleVaccines(d.cumulative_vaccines)+labelWidth+5;
    })
    .attr('opacity', function(d,i){
        var val = 0;
        if(options.region_chart_toggle == 'cases') val = (d.cumulative_cases);
        if(options.region_chart_toggle == 'deaths') val = (d.cumulative_deaths);
        if(options.region_chart_toggle == 'vaccines') val = (d.cumulative_vaccines);
        if(val>0){ return 1 } else { return 0}
    })
	.style('fill', '#696969')
	.style('font-weight', 'normal')
	.style('font-size', '10px');

}

