		d3.select(this).style('opacity', 0.1)
	})
	.on('mouseout', function(){
		d3.select(this).style('opacity', 0)
	})
	.on('click', function(d,i){
        console.log('filter');
    });

    const xScaleCases = d3.scaleLinear()
	.range([0, rcSize.width - margin.right- labelWidth])
	.domain([0, maxCases]);// severity/reliability x xcale

    const xScaleDeaths = d3.scaleLinear()
	.range([labelWidth, rcSize.width - margin.right])
	.domain([0, maxDeaths]);// severity/reliability x xcale

    console.log(svg);
    const barPadding = 0.13;

    var bar = rows.append('rect')
    .attr('id', function(d,i){
        return 'bar-'+d.region;
    })
    .attr('class', 'region-bar')
    .attr('width', function(d,i){
        return xScaleCases(d.cumulative_cases);
    })
    .attr('x', labelWidth)
    .style('outline', 'none')
    .style('cursor', 'pointer')
    .attr('y', rowHeight*barPadding - 1)
    .attr('height', rowHeight-((rowHeight*barPadding)*2))
