import colors from './colors';
import {addCommas} from './HelperFunctions';
import {filter} from './App';

let maxX;
let maxY;
let xScale; 
let yScale; 
let groupedData = [];
let width = 600;
let height = 250;
let x = 18;
let y = 54;

export function drawBubblechart(data, options, filter) {

    maxX = d3.max(groupedData, function(d,i){
        return d.cumulative_cases;
    })

    maxY = d3.max(groupedData, function(d,i){
        return d.cumulative_cases;
    })

    xScale = d3.scaleLinear()
	.range([0, width])
	.domain([0, maxX]);

    yScale = d3.scaleLinear()
	.range([0, height])
	.domain([0, maxY]);

    const svg = d3.select('#bubblechart-tab').append('g').attr('transform', 'translate('+x+', '+y+')')
    .attr('id', 'bubblechart')
    .append('rect')
    .attr('width', width)
    .attr('height', height)
    .attr('fill', 'steelblue');

    var rows = svg.selectAll('.bar-row')
	.data(groupedData.filter(d=>d.region!=='OTH'))
	.enter()
	.append('g')
	.attr('class', 'bar-row')
	.attr('transform', function(d,i){
		// return 'translate(0,' + ((i*rowHeight) + margin.top) + ')';
	});

}

