        return new Date(a.Date_reported) - new Date(b.Date_reported);
    });

    data.who_data.forEach(function(d,i){

        d.date = new Date(d.Date_reported);
        d.dayOfYear =  moment(d.date).dayOfYear();
        if(d.dayOfYear!==dayCheck) dayTotal++;
        d.dayTotal = dayTotal;

        d.week = moment(d.date).isoWeek();
        if(d.week!==weekCheck) weekTotal++;
        d.weekTotal = weekTotal;
        d.month = d.date.getMonth();
        if(d.month!==monthCheck) monthTotal++;
        d.monthTotal = monthTotal;
        d.year = d.date.getFullYear();
        d.new_cases = parseInt(d.New_cases);
        d.new_deaths = parseInt(d.New_deaths);
        d.cumulative_deaths = parseInt(d.Cumulative_deaths);
        d.cumulative_cases = parseInt(d.Cumulative_cases);
        d.country_code = d.Country_code;
        d.country_name = null;
        data.equity_data.forEach(function(dd,ii){
            if(dd.country_iso2===d.Country_code){
                d.ifrc_region = dd.ifrc_region;
                d.country_name = dd.country_name;
            }
        })
        if(d.ifrc_region==undefined) d.ifrc_region = 'OTH';
        delete d.Date_reported;
        delete d.Country;
        delete d.New_cases;
        delete d.New_deaths;
        delete d.Country_code;
        delete d.Cumulative_deaths;
        delete d.Cumulative_cases;
        // delete d.Country_code;
        delete d.WHO_region;
        // delete d.Country;
        dayCheck = d.dayOfYear;
        weekCheck = d.week;
        monthCheck = d.month;
    })

    // parse vaccine cumulative data

    dayTotal = -1;
    dayCheck = 0;

    weekTotal = -1;
    weekCheck = -1;
    monthTotal = 0;
    monthCheck = 0;

    // filter out vaccine data on only include records with ISO3 country codes
    data.vaccines_data_cumulative = data.vaccines_data.filter(function(d,i){
        return d.iso_code.length===3 && d.total_vaccinations > 0;
    })

    // fill out missing vaccinations date records (extend cumulative)
    var vGroup = flatGroup(data.vaccines_data_cumulative, d => d.iso_code);

    var newDates = [];
    var hasDays = [];
    var initDate = 0;
    var lastRecord = {};
    var vDataFlat = [];
    vGroup.forEach(function(d,i){

        var minDt = new Date(minDateVaccines);
        var maxDt = new Date(maxDateVaccines);
        var hasDays = [];
        var newDays = [];
        var newDays = [];

