        if(t==='day') groupedData[d[t+'Total']].date = d.date;
        if(t==='week') groupedData[d[t+'Total']].date = moment(d.date).startOf('isoWeek').toDate();
        if(t==='month') groupedData[d[t+'Total']].date = moment(d.date).startOf('month').toDate();
        if(!groupedData[d[t+'Total']][d.ifrc_region]) groupedData[d[t+'Total']][d.ifrc_region] = {new_cases: 0, new_deaths: 0, cumulative_cases: 0, cumulative_deaths: 0};
        groupedData[d[t+'Total']][d.ifrc_region].new_cases += d.new_cases;
        groupedData[d[t+'Total']][d.ifrc_region].new_deaths += d.new_deaths;
        groupedData[d[t+'Total']][d.ifrc_region].cumulative_cases = 0;

