// import * as d3 from "d3";
import {min,max,group,flatGroup} from "d3";
import moment from 'moment';
import clone from 'just-clone';
// const regions = ['AF', 'ME', 'AP', 'EU', 'AM', 'OTH'];
const regions = [
    {'code': 'AF', 'population': 0},
    {'code': 'ME', 'population': 0},
    {'code': 'AP', 'population': 0},
    {'code': 'EU', 'population': 0},
    {'code': 'AM', 'population': 0},
    {'code': 'OTH', 'population': 0}
]
export function ParseData(data, options) {

    data.equity_data.forEach(function(d,i){
        d.vaccines_used = [];
        d.population = parseInt(d.population);
