// import * as d3 from "d3";
import {min,max,group,flatGroup} from "d3";
import moment from 'moment';
import clone from 'just-clone';

export function ParseData(data, options) {

    // console.log('ParseData');
    // console.log(options);

    // max dates
    let maxDate = moment(new Date(max(data.who_data, function(d){
        return d.Date_reported;
    })));

    let minDate = moment(new Date(min(data.who_data, function(d){
        return d.Date_reported;
    })));

    let minDateVaccines = (new Date(min(data.vaccines_data, function(d){
        return d.date;
    })));


    let maxDateVaccines = (new Date(max(data.vaccines_data, function(d){
        return d.date;
    })));

    if(maxDateVaccines>maxDate) {
        maxDate = maxDateVaccines;
    }

    options.maxDate = moment(maxDate).toDate();
    options.maxVDate = moment(maxDateVaccines).toDate();
    options.minDate = moment(minDate).toDate();

    // parse who data

    let dayTotal = -1;
    let dayCheck = 0;

    let weekTotal = -1;
    let weekCheck = -1;

    let monthTotal = 0;
    let monthCheck = 0;

    data.who_data.sort(function(a,b){
        return new Date(a.Date_reported) - new Date(b.Date_reported);
    });

    var countryCodes = [];

    data.who_data.forEach(function(d,i){
        d.date = new Date(d.Date_reported);
        d.dayOfYear =  moment(d.date).dayOfYear();
        if(d.dayOfYear!==dayCheck) dayTotal++;
        d.dayTotal = dayTotal;

        d.week = moment(d.date).isoWeek();
        if(d.week!==weekCheck) weekTotal++;
        d.weekTotal = weekTotal;
        d.month = d.date.getMonth();
        if(d.month!==monthCheck) monthTotal++;
        d.monthTotal = monthTotal;
        d.year = d.date.getFullYear();

        d.week_start = new Date(moment(d.date).startOf('isoWeek').toDate());
        d.month_start = new Date(moment(d.date).startOf('month').toDate());

        d.new_cases = parseInt(d.New_cases);
        d.new_deaths = parseInt(d.New_deaths);
        d.cumulative_deaths = parseInt(d.Cumulative_deaths);
        d.cumulative_cases = parseInt(d.Cumulative_cases);
        d.country_code = d.Country_code;
        d.country_name = null;
        data.equity_data.forEach(function(dd,ii){
            if(dd.country_iso2===d.Country_code){
                d.ifrc_region = dd.ifrc_region;
                d.country_name = dd.country_name;
            }
        })
        if(d.ifrc_region==undefined) d.ifrc_region = 'OTH';
        if(!countryCodes.includes(d.country_code)) countryCodes.push(d.country_code);

        delete d.Date_reported;
        delete d.Country;
        delete d.New_cases;
        delete d.New_deaths;
        delete d.Country_code;
        delete d.Cumulative_deaths;
        delete d.Cumulative_cases;
        // delete d.Country_code;
        delete d.WHO_region;
        // delete d.Country;
        dayCheck = d.dayOfYear;
        weekCheck = d.week;
        monthCheck = d.month;
    })

    // parse vaccine cumulative data

    dayTotal = -1;
    dayCheck = 0;

    weekTotal = -1;
    weekCheck = -1;
    monthTotal = 0;
    monthCheck = 0;

    // filter out vaccine data on only include records with ISO3 country codes
    data.vaccines_data_cumulative = data.vaccines_data.filter(function(d,i){
        return d.iso_code.length===3 && d.total_vaccinations > 0;
    })

    // fill out missing vaccinations date records (extend cumulative)
    var vGroup = flatGroup(data.vaccines_data_cumulative, d => d.iso_code);

    var newDates = [];
    var hasDays = [];
    var initDate = 0;
    var lastRecord = {};
    var vDataFlat = [];
    vGroup.forEach(function(d,i){

        var minDt = new Date(minDateVaccines);
        var maxDt = new Date(maxDateVaccines);
        var hasDays = [];
        var newDays = [];
        var newDays = [];        
        data.equity_data.forEach(function(dd,ii){
            if(dd.country_iso3===vGroup[i][0]){
                d.ifrc_region = dd.ifrc_region;
                d.country_name = dd.country_name;
                d.country_code = dd.country_iso2;

            }
        })

        vGroup[i].lastDate = new Date(max(vGroup[i][1], function(d,i){ return d.date }));

        vGroup[i][1].forEach(function(dd,ii){
            hasDays.push(new Date(dd.date).getTime());
        })

        // if((i<1)||(d[0] =='BFA')||(d[0] =='RWA')){
        vGroup[i][1].forEach(function(dd,ii){
            dd.total_vaccinations = parseInt(dd.total_vaccinations);
            for (var day = minDt;day <= maxDt; day.setDate(day.getDate() + 1)) {
                var newDay = day.getTime();
                if(!hasDays.includes(newDay)){
                    newDays.push({date: new Date(day), total_vaccinations: null})
                }
            }
            // dd.lastDate = dd.date;
        });

        vGroup[i][1].push(...newDays);

        vGroup[i][1].sort(function(a,b){
            return new Date(a.date) - new Date(b.date);
        });

        var totalVac = 0;
        var totalVac100 = 0;
        var people_vaccinated_per_hundred = 0;
        var people_fully_vaccinated_per_hundred = 0;

        vGroup[i][1].forEach(function(dd,ii){

            if(dd.total_vaccinations){
                totalVac = dd.total_vaccinations;
            } else {
                dd.total_vaccinations = totalVac;
            }
            if(dd.total_vaccinations_per_hundred){
                totalVac100 = dd.total_vaccinations_per_hundred
            } else {
                dd.total_vaccinations_per_hundred = totalVac100;
            }
            if(dd.people_vaccinated_per_hundred){
                people_vaccinated_per_hundred = dd.people_vaccinated_per_hundred;
            } else {
                dd.people_vaccinated_per_hundred = people_vaccinated_per_hundred;
            }
            if(dd.people_fully_vaccinated_per_hundred){
                people_fully_vaccinated_per_hundred = dd.people_fully_vaccinated_per_hundred;
            } else {
                dd.people_fully_vaccinated_per_hundred = people_fully_vaccinated_per_hundred;
            }

            var countryName;
            if(vGroup[i].country_name==null) { countryName = vGroup[i][0];
            } else {
                countryName = vGroup[i].country_name
            }

            vDataFlat.push({
                date: new Date(dd.date),
                lastDate: (vGroup[i].lastDate),
                total_vaccinations_per_hundred: parseFloat(totalVac100),
                people_vaccinated_per_hundred: people_vaccinated_per_hundred,
                people_fully_vaccinated_per_hundred: people_fully_vaccinated_per_hundred,
                iso_code: vGroup[i][0], 
                ifrc_region: vGroup[i].ifrc_region,
                country_name: countryName,
                country_code: vGroup[i].country_code, 
                total_vaccinations: totalVac 
            })
        })
    });

    data.vaccines_data_cumulative = vDataFlat;

    data.vaccines_data_cumulative.sort(function(a,b){
        return new Date(a.date) - new Date(b.date);
    });

    data.vaccines_data_cumulative.forEach(function(d,i){
        if(d.ifrc_region==undefined) d.ifrc_region = 'OTH';
        d.dayOfYear =  moment(d.date).dayOfYear();
        if(d.dayOfYear!==dayCheck) dayTotal++;
        d.dayTotal = dayTotal;
        d.week = moment(d.date).isoWeek();
        if(d.week!==weekCheck) weekTotal++;
        d.weekTotal = weekTotal;
        d.month = d.date.getMonth();
        if(d.month!==monthCheck) monthTotal++;
        d.monthTotal = monthTotal;
        d.year = d.date.getFullYear();
        dayCheck = d.dayOfYear;
        weekCheck = d.week;
        monthCheck = d.month;
        d.week_start = new Date(moment(d.date).startOf('isoWeek').toDate());
        d.month_start = new Date(moment(d.date).startOf('month').toDate());
    })

    // parse vaccine daily data

    dayTotal = -1;
    dayCheck = 0;

    weekTotal = -1;
    weekCheck = -1;
    monthTotal = 0;
    monthCheck = 0;

    // filter out vaccine data on only include records with ISO3 country codes
    data.vaccines_data_daily = data.vaccines_data.filter(function(d,i){
        return d.iso_code.length===3 && d.daily_vaccinations > 0;
    })

    // fill out missing vaccinations date records (extend cumulative)
    var vGroup = flatGroup(data.vaccines_data_daily, d => d.iso_code);

    var newDates = [];
    var hasDays = [];
    var initDate = 0;
    var lastRecord = {};
    var vDataFlat = [];
    vGroup.forEach(function(d,i){

        var minDt = new Date(minDateVaccines);
        var maxDt = new Date(maxDateVaccines);
        var hasDays = [];
        var newDays = [];
        var newDays = [];

        data.equity_data.forEach(function(dd,ii){
            if(dd.country_iso3===vGroup[i][0]){
                d.ifrc_region = dd.ifrc_region;
                d.country_name = dd.country_name;
                d.country_code = dd.country_iso2;
            }
        })

        vGroup[i][1].forEach(function(dd,ii){
            hasDays.push(new Date(dd.date).getTime());
        })

        // if((i<1)||(d[0] =='BFA')||(d[0] =='RWA')){
        vGroup[i][1].forEach(function(dd,ii){
            dd.daily_vaccinations = parseInt(dd.daily_vaccinations);
            for (var day = minDt;day <= maxDt; day.setDate(day.getDate() + 1)) {
                var newDay = day.getTime();
                if(!hasDays.includes(newDay)){
                    newDays.push({date: new Date(day), daily_vaccinations: null})
                }
            }
        });

        vGroup[i][1].push(...newDays);

        vGroup[i][1].sort(function(a,b){
            return new Date(a.date) - new Date(b.date);
        });

        var daily_vaccinations = 0;

        vGroup[i][1].forEach(function(dd,ii){
            if(dd.daily_vaccinations){
                daily_vaccinations = dd.daily_vaccinations;
            } else {
                dd.daily_vaccinations = daily_vaccinations;
            }
            var countryName;
            if(vGroup[i].country_name==null) { countryName = vGroup[i][0];
            } else {
                countryName = vGroup[i].country_name
            }
            vDataFlat.push({
                date: new Date(dd.date),
                iso_code: vGroup[i][0], 
                ifrc_region: vGroup[i].ifrc_region,
                country_name: countryName,
                country_code: vGroup[i].country_code, 
                daily_vaccinations: daily_vaccinations 
            })
        })
    });

    data.vaccines_data_daily = vDataFlat;

    data.vaccines_data_daily.sort(function(a,b){
        return new Date(a.date) - new Date(b.date);
    });

    data.vaccines_data_daily.forEach(function(d,i){
        if(d.ifrc_region==undefined) d.ifrc_region = 'OTH';
        d.dayOfYear =  moment(d.date).dayOfYear();
        if(d.dayOfYear!==dayCheck) dayTotal++;
        d.dayTotal = dayTotal;
        d.week = moment(d.date).isoWeek();
        if(d.week!==weekCheck) weekTotal++;
        d.weekTotal = weekTotal;
        d.month = d.date.getMonth();
        if(d.month!==monthCheck) monthTotal++;
        d.monthTotal = monthTotal;
        d.year = d.date.getFullYear();
        dayCheck = d.dayOfYear;
        weekCheck = d.week;
        monthCheck = d.month;
        d.week_start = new Date(moment(d.date).startOf('isoWeek').toDate());
        d.month_start = new Date(moment(d.date).startOf('month').toDate());
    });


    // parse equity data inform/hdi indexes
    data.equity_data.forEach(function(d,i){
        if(d.hdi_category.match(/very high/i)){
            d.hdi_category = 'very-high'
        }
        else if(d.hdi_category.match(/high/i)){
            d.hdi_category = 'high'
        }
        else if(d.hdi_category.match(/medium/i)){
            d.hdi_category = 'medium'
        }
        else if(d.hdi_category.match(/low/i)){
            d.hdi_category = 'low'
        }
        if(d.inform_severity.match(/very high/i)){
            d.inform_severity = 'very-high'
        }
        else if(d.inform_severity.match(/high/i)){
            d.inform_severity = 'high'
        }
        else if(d.inform_severity.match(/medium/i)){
            d.inform_severity = 'medium'
        }
        else if(d.inform_severity.match(/low/i)){
            d.inform_severity = 'low'
        }
        else if(d.inform_severity.match(/no/i)){
            d.inform_severity = 'no-score'
        }
    })

    data.equity_data = data.equity_data.filter(function(d,i){
       return countryCodes.includes(d.country_iso2)
    })
    data.original = clone(data);
    // console.log(data);
}

export default ParseData;
