    d3.select('#inform1val-medium tspan').text(parse(informTotalCasesData['medium'] / totalCases * 100).toFixed(0) + '%').style('text-anchor', 'end').attr('dx', '16');
    d3.select('#inform1bar-medium').transition().duration(500).attr('width', parse(informTotalCasesData['medium'] / totalCases) * 64);
    d3.select('#inform1val-low tspan').text(parse(informTotalCasesData['low'] / totalCases * 100).toFixed(0) + '%').style('text-anchor', 'end').attr('dx', '16');
    d3.select('#inform1bar-low').transition().duration(500).attr('width', parse(informTotalCasesData['low'] / totalCases) * 64);
    d3.select('#inform1val-no-score tspan').text(parse(informTotalCasesData['no-score'] / totalCases * 100).toFixed(0) + '%').style('text-anchor', 'end').attr('dx', '16');
    d3.select('#inform1bar-no-score').transition().duration(500).attr('width', parse(informTotalCasesData['no-score'] / totalCases) * 64); // num countries filtered (table)

    d3.select('#countries_filtered tspan').text(numCountriesFiltered + ' countries filtered').style('text-anchor', 'end').attr('dx', '105');
    d3.select('#total_new_cases tspan').text('-');
    d3.select('#total_vac_100 tspan').text('-');
    d3.select('#percent_acceptance tspan').text('-');
    d3.select('#total_cases tspan').transition().duration(500).textTween(function () {
      return function (t) {
        return nFormatter(d3.interpolate(0, totalCases)(t));
      };
    });
    d3.select('#total_new_cases tspan').transition().duration(500).textTween(function () {
      return function (t) {
        return nFormatter(d3.interpolate(0, totalCases - newCases)(t));
      };
    });
    d3.select('#total_deaths tspan').transition().duration(500).textTween(function () {
      return function (t) {
        return nFormatter(d3.interpolate(0, totalDeaths)(t));
      };
    });
    d3.select('#total_vaccines tspan').transition().duration(500).textTween(function () {
      return function (t) {
        return nFormatter(d3.interpolate(0, d3.sum(data.cumulative_totals, d => d.cumulative_vaccines))(t));
      };
    });
    d3.select('#total_ns_countries tspan').transition().duration(500).textTween(function () {
      return function (t) {
        return Math.round(d3.interpolate(0, numNsInvolvedCountres)(t));
      };
    });
    d3.select('#num_vaccines tspan').transition().duration(500).textTween(function () {
      return function (t) {
        return Math.round(d3.interpolate(0, data.vaccine_types.length)(t));
      };
    });

    if (vaccinePer100ByCountry) {
      d3.select('#total_vac_100 tspan').transition().duration(500).textTween(function () {
        return function (t) {
          return d3.interpolate(0, vaccinePer100ByCountry)(t).toFixed(1);
        };
      });
    }

    d3.select('#percent_acceptance tspan').transition().duration(500).textTween(function () {
      return function (t) {
        return Math.round(d3.interpolate(0, vaccineAcceptancePercent)(t)) + '%';
      };
    });
  }

  function updateTable(data, options) {
    var lastRecord = data.vaccines_data_cumulative.filter(d => d.date.getTime() == options.maxVDate.getTime());
    var vGroup = d3.flatGroup(lastRecord, d => d.iso_code);
    data.equity_data.forEach(function (d, i) {
      var vaccince_acceptance_percent = d.vaccince_acceptance_percent || 0;

      if (!vGroup.some(item => item[0] === d.country_iso3)) {
        vGroup.push([d.country_iso3, [{
          country_name: d.country_name,
          vaccince_acceptance_percent: vaccince_acceptance_percent
        }]]);
      }

      vGroup.forEach(function (dd, ii) {
        if (dd[0] == d.country_iso3) {
          dd[1][0].vaccince_acceptance_percent = vaccince_acceptance_percent;
          dd[1][0].country_name = d.country_name;
        }
      });
    });
    var sortKey;
    if (options.table_sort == 'col0_sort') sortKey = 'country_name';
    if (options.table_sort == 'col1_sort') sortKey = 'total_vaccinations_per_hundred';
    if (options.table_sort == 'col2_sort') sortKey = 'people_vaccinated_per_hundred';
    if (options.table_sort == 'col3_sort') sortKey = 'people_fully_vaccinated_per_hundred';
    if (options.table_sort == 'col4_sort') sortKey = 'vaccince_acceptance_percent';
    if (options.table_sort == 'col5_sort') sortKey = 'total_vaccinations';

    for (var x = 0; x < 6; x++) {
      d3.select('#col' + x + '_sort_desc').attr('opacity', 0);
      d3.select('#col' + x + '_sort_asc').attr('opacity', 0);
    }

    d3.select('#' + options.table_sort + '_' + options.table_sort_direction).attr('opacity', 1);

    if (sortKey == 'country_name') {
      if (options.table_sort_direction == 'asc') {
        vGroup.sort(function (b, a) {
          return a[1][0].country_name.localeCompare(b[1][0].country_name);
        });
      } else {
        vGroup.sort(function (a, b) {
          return a[1][0].country_name.localeCompare(b[1][0].country_name);
        });
      }
    } else {
      if (options.table_sort_direction == 'desc') {
        vGroup.sort(function (a, b) {
          return b[1][0][sortKey] - a[1][0][sortKey];
        });
      } else {
        vGroup.sort(function (b, a) {
          return b[1][0][sortKey] - a[1][0][sortKey];
        });
      }
    }

    data.vGroup = vGroup;
    data.vGroup.forEach(function (d, i) {
      data.equity_data.forEach(function (dd, ii) {
