  function updateTotals(data,options){
    var totalCases = d3.sum(data.cumulative_totals, d=>d.cumulative_cases);
    var totalDeaths = d3.sum(data.cumulative_totals, d=>d.cumulative_deaths);
    var numCountriesFiltered = d3.flatGroup(data.who_data, d => d.country_code).length;
    var countries = d3.flatGroup(data.who_data, d => d.country_code);

    data.country = [];
    countries.forEach(function(d,i){
      var cases4weeksAgo = d[1][d[1].length-28].cumulative_cases;
      var cases2WeeksAgo = d[1][d[1].length-14].cumulative_cases;
      var cases1WeeksAgo = d[1][d[1].length-7].cumulative_cases;
      var casesLatest = d[1][d[1].length-1];
      var percentChange = parseFloat(((casesLatest.cumulative_cases - cases2WeeksAgo)-(cases2WeeksAgo-cases4weeksAgo))/cases4weeksAgo);
      if(isFinite(percentChange)) { casesLatest.percent_change = percentChange; } else { casesLatest.percent_change = 0 }
      data.country.push([ d[0], casesLatest, cases2WeeksAgo, cases4weeksAgo, cases1WeeksAgo])
    });

    // update hdi numbers
    var hdiData = {'very-high':0, 'high': 0, 'medium': 0, 'low': 0};
    var hdiDataPopulation = {'very-high':0, 'high': 0, 'medium': 0, 'low': 0};
    var hdiTotalCasesData = {'very-high':0, 'high': 0, 'medium': 0, 'low': 0};
    var informData = {'very-high':0, 'high': 0, 'medium': 0, 'low': 0, 'no-score': 0};
    var informDataPopulation = {'very-high':0, 'high': 0, 'medium': 0, 'low': 0, 'no-score': 0};
    var informTotalCasesData = {'very-high':0, 'high': 0, 'medium': 0, 'low': 0, 'no-score': 0};
    var totalRecordedPopulation = 0;

    data.vaccine_types = [];
    var vaccinesper100 = [];
    var vaccineper100byPop = {'total_population': 0, 'vaccine_population': 0};

    var numNsInvolvedCountres = 0;
    var vaccineAcceptance = {'total_population': 0, 'accept_population': 0};
    if(totalCases>0){
      data.equity_data.forEach(function(dd,ii){
          dd.vaccines_used.forEach(function(ddd){
            if(!data.vaccine_types.includes(ddd)) data.vaccine_types.push(ddd);
          })
          hdiData[dd.hdi_category] += 1;
          hdiDataPopulation[dd.hdi_category] += parseInt(dd.population);
          numNsInvolvedCountres += dd.ns_involvement;
          informData[dd.inform_severity] += 1;
          informDataPopulation[dd.inform_severity] += dd.population;
          var dat = countries.filter((d)=>d[0]==dd.country_iso2);
          if((dat[0][1].length>0)&&(dat[0])){
            var lastRecord = dat[0][1][dat[0][1].length-1];
            // hdi index
            hdiTotalCasesData[dd.hdi_category] += lastRecord.cumulative_cases;
            informTotalCasesData[dd.inform_severity] += lastRecord.cumulative_cases;
          }
        // vaccine per 100 by country average
        if(dd.vaccines_per_100>0) vaccinesper100.push([dd.vaccines_per_100,dd.population]);
        // vaccine per 100 by population
        var population = 0;
        if(dd.vaccince_acceptance_population){
          population = dd.vaccince_acceptance_population;
        } else if (dd.population){
          population = dd.population;
        }
        if((dd.vaccines_per_100>0)&&(population>0)) vaccineper100byPop.total_population += parseInt(population);
        if((dd.vaccines_per_100>0)&&(population>0)) vaccineper100byPop.vaccine_population += (population * (dd.vaccines_per_100/100));
        // vaccine acceptance
          if(dd.vaccince_acceptance_population>0) vaccineAcceptance.total_population += dd.vaccince_acceptance_population;
          if(dd.vaccince_acceptance_population>0) vaccineAcceptance.accept_population += (dd.vaccince_acceptance_population * dd.vaccince_acceptance_percent);
      });
    }

    console.log(data.equity_data);

    // vaccine acceptance percent
    var vaccineAcceptancePercent = ((vaccineAcceptance.accept_population / vaccineAcceptance.total_population)).toFixed(1);

    // vaccine doses per 100
    var vaccinePer100ByPopulation = ((vaccineper100byPop.vaccine_population / vaccineper100byPop.total_population)*100).toFixed(1);
    var vaccinePer100ByCountry = d3.mean(data.vGroup, d=>d[1][0].total_vaccinations_per_hundred);

    d3.selectAll('.vaccine_type').remove();
    d3.select('#vaccine_tooltip_bg').attr('height', 1);
    data.vaccine_types.forEach(function(d,i){
      d3.select("#vaccine_tooltip").append('text')
      .attr('font-size', 9)
      .attr('fill', '#000')
      .attr('class', 'vaccine_type')
      .append('tspan').text(d)
      .attr('dx', 6)
      .attr('dy',((i*1.3)+1.8)+'em')
    });

    var vtooltipbbox = d3.select('#vaccine_tooltip').node().getBBox();
    d3.select('#vaccine_tooltip_bg').attr('height', vtooltipbbox.height+6);

    // new cases
    var newCases = d3.sum(data.country, d => d[4])

    // var totalVaccines = d3.sum(data.cumulative_totals, d=>d.cumulative_vaccines));
    var totalVaccines = d3.sum(data.equity_data, d=>d.total_vaccinations);

    // hide bars 
    if(totalCases==0){
      d3.selectAll('.bar_group').attr('opacity',0);
    } else {
      d3.selectAll('.bar_group').attr('opacity',1);
    }

    var totalHdiPop = hdiDataPopulation['very-high']+hdiDataPopulation['high']+hdiDataPopulation['medium']+hdiDataPopulation['low'];
    var totalInformPop = informDataPopulation['very-high']+informDataPopulation['high']+informDataPopulation['medium']+informDataPopulation['low']+informDataPopulation['no-score'];

    d3.select('#hdi-percent-val-very-high tspan').text(parse((hdiDataPopulation['very-high']/totalHdiPop)*100).toFixed(0)+'%').style('text-anchor', 'end').attr('dx', '16');
    d3.select('#hdi-percent-bar-very-high').transition().duration(500).attr('width', (parse(hdiDataPopulation['very-high']/totalHdiPop)*70));
    d3.select('#hdi-percent-val-high tspan').text(parse((hdiDataPopulation['high']/totalHdiPop)*100).toFixed(0)+'%').style('text-anchor', 'end').attr('dx', '16');
