    d3.select('#hdi-percent-val-low tspan').text(parse((hdiDataPopulation['low']/totalHdiPop)*100).toFixed(0)+'%').style('text-anchor', 'end').attr('dx', '16');
    d3.select('#hdi-percent-bar-low').transition().duration(500).attr('width', (parse(hdiDataPopulation['low']/totalHdiPop)*70));

    d3.select('#inform-percent-val-very-high tspan').text(parse((informDataPopulation['very-high']/totalInformPop)*100).toFixed(0)+'%').style('text-anchor', 'end').attr('dx', '16');
    d3.select('#inform-percent-bar-very-high').transition().duration(500).attr('width', (parse(informDataPopulation['very-high']/totalInformPop)*64));
    d3.select('#inform-percent-val-high tspan').text(parse((informDataPopulation['high']/totalInformPop)*100).toFixed(0)+'%').style('text-anchor', 'end').attr('dx', '16');
    d3.select('#inform-percent-bar-high').transition().duration(500).attr('width', (parse(informDataPopulation['high']/totalInformPop)*64));
