  function updateTable(data, options){

    var lastRecord = data.vaccines_data_cumulative.filter(d=>d.date.getTime()==options.maxVDate.getTime());
    var vGroup = d3.flatGroup(lastRecord, d => d.iso_code);

    data.equity_data.forEach(function(d,i){
      var vaccince_acceptance_percent = (d.vaccince_acceptance_percent) || 0
      if(!vGroup.some(item => item[0] === d.country_iso3)){
        vGroup.push([d.country_iso3, [{
          country_name: d.country_name,
          vaccince_acceptance_percent: vaccince_acceptance_percent
        }]])
      }
      vGroup.forEach(function(dd,ii){
        if(dd[0]==d.country_iso3){
          dd[1][0].vaccince_acceptance_percent = vaccince_acceptance_percent;
          dd[1][0].country_name = d.country_name;
        }
      })
    })

    var sortKey;
    if(options.table_sort=='col0_sort') sortKey = 'country_name';
    if(options.table_sort=='col1_sort') sortKey = 'total_vaccinations_per_hundred';
    if(options.table_sort=='col2_sort') sortKey = 'people_vaccinated_per_hundred';
    if(options.table_sort=='col3_sort') sortKey = 'people_fully_vaccinated_per_hundred';
    if(options.table_sort=='col4_sort') sortKey = 'vaccince_acceptance_percent';
    if(options.table_sort=='col5_sort') sortKey = 'total_vaccinations';

    for(var x = 0; x<6; x++){
      d3.select('#col'+x+'_sort_desc').attr('opacity', 0);
      d3.select('#col'+x+'_sort_asc').attr('opacity', 0);
    }

    d3.select('#'+options.table_sort+'_'+options.table_sort_direction)
    .attr('opacity', 1);

    if(sortKey=='country_name'){
      if(options.table_sort_direction=='asc') {
        vGroup.sort(function(b, a){
          return a[1][0].country_name.localeCompare(b[1][0].country_name);
        })
      } else {
        vGroup.sort(function(a, b){
          return a[1][0].country_name.localeCompare(b[1][0].country_name);
        })    
      }

    } else {
      if(options.table_sort_direction=='desc') {
        vGroup.sort(function(a,b){
            return b[1][0][sortKey] - a[1][0][sortKey];
        });
      } else {
        vGroup.sort(function(b,a){
          return b[1][0][sortKey] - a[1][0][sortKey];
        });
      }
    }

    data.vGroup = vGroup;

    data.vGroup.forEach(function(d,i){
      data.equity_data.forEach(function(dd,ii){
          if(d[0] == dd.country_iso3){
              dd.people_fully_vaccinated_per_hundred = parseFloat(d[1][0].people_fully_vaccinated_per_hundred);
              dd.people_vaccinated_per_hundred = parseFloat(d[1][0].people_vaccinated_per_hundred);
              dd.total_vaccinations = parseFloat(d[1][0].total_vaccinations);
              dd.total_vaccinations_per_hundred = parseFloat(d[1][0].total_vaccinations_per_hundred);
          }
      })
  })


    d3.selectAll('.tablerow').remove();
    d3.selectAll('.col1bar').attr('opacity', 0);
    d3.selectAll('.col2bar').attr('opacity', 0);
    d3.selectAll('.col3bar').attr('opacity', 0);
    d3.selectAll('.col4bar').attr('opacity', 0);
    d3.selectAll('.colbg').attr('opacity', 0);

    for(let i = 0; i < 10; i++){
      var d = vGroup[i];
      if(!vGroup[i]) {
        d3.select('#col1bar'+i)
        .attr('width', 0);
        d3.select('#col2bar'+i)
        .attr('width', 0)
        d3.select('#col3bar'+i)
        .attr('width', 0)
        d3.select('#col4bar'+i)
        .attr('width', 0)
        continue;
      }

      d3.select('#vac-tab').append('text')
      .attr('class','tablerow table_country_name')
      .text(function(){
        var name;
        if(d[1][0].country_name) { name = d[1][0].country_name; } else {
          name = d[1][0].iso_code;
        }
        name = name.substring(0,17);
        if(name.length==17)name=name+'.';
        return name;
      })
      .attr('x', 0)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 1 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow table_country_name')
      .text(function(){
        var val = parseFloat(d[1][0].total_vaccinations_per_hundred).toFixed(1);
        if((isNaN(val))||(val==0)){ return ''} else { return val }
      })
      .style('text-anchor', 'end')
      .attr('x', 112)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 1 bar
      d3.select('#col1bar'+i)
      .attr('opacity', 1)
      .transition()
      .duration(500)
      .attr('width', function(){
        var val = d[1][0].total_vaccinations_per_hundred;
        if(d[1][0].total_vaccinations_per_hundred>100){
          val = 100
        } 
        if(isNaN(val)){ return 0} else { return 68*(val/100) }
      });

      d3.select('#col1bg'+i)
      .attr('opacity', 1);

      // col 2 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = parseFloat(d[1][0].people_vaccinated_per_hundred).toFixed(1);
        if((isNaN(val))||(val==0)){ return ''} else { return val+'%' }
      })
      .style('text-anchor', 'end')
      .attr('x', 223)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 2 bar
      d3.select('#col2bar'+i)
      .attr('opacity', 1)
      .transition()
      .duration(500)
      .attr('width', function(){
        var val = d[1][0].people_vaccinated_per_hundred;
        if(d[1][0].people_vaccinated_per_hundred>100){
          val = 100
        } 
        if(isNaN(val)){ return 0} else { return 68*(val/100) }
      });

      d3.select('#col2bg'+i)
      .attr('opacity', 1);

      // col 3 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = parseFloat(d[1][0].people_fully_vaccinated_per_hundred).toFixed(1);
        if((isNaN(val))||(val==0)){ return ''} else { return val+'%' }
      })
      .style('text-anchor', 'end')
      .attr('x', 335)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 3 bar
      d3.select('#col3bar'+i)
      .attr('opacity', 1)
      .transition()
      .duration(500)
      .attr('width', function(){
        var val = d[1][0].people_fully_vaccinated_per_hundred;
        if(d[1][0].people_fully_vaccinated_per_hundred>100){
          val = 100
        } 
        if(isNaN(val)){ return 0} else { return 68*(val/100) }
      });

      d3.select('#col3bg'+i)
      .attr('opacity', 1);

      // col 4 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = parseFloat(d[1][0].vaccince_acceptance_percent).toFixed(1);
        if((isNaN(val))||(val==0)){ return ''} else { return val+'%' }
      })
      .style('text-anchor', 'end')
      .attr('x', 450)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 4 bar
      d3.select('#col4bar'+i)
      .attr('opacity', 1)
      .transition()
      .duration(500)
      .attr('width', function(){
        var val = d[1][0].vaccince_acceptance_percent;
        if(d[1][0].vaccince_acceptance_percent>100){
          val = 100
        } 
        if(isNaN(val)){ return 0} else { return 68*(val/100) }
      });

      d3.select('#col4bg'+i)
      .attr('opacity', 1);


      var dayFormat = d3.timeFormat("%b %d");
      // col 5 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = (d[1][0].total_vaccinations);
        if((isNaN(val))||(val==0)){ return ''} else { return nFormatter(val) }
      })
      .style('text-anchor', 'end')
      .attr('x', 572)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 6 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = (d[1][0].lastDate);
        if((val)){ return dayFormat(val)} else { return '' }
      })
      .style('text-anchor', 'end')
      .attr('x', 620)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

    }

  }

  function createSelects(){
     // region select
      $('#regionSelect').select2({
        data: [{
          id: 'regions',
          text: 'IFRC Regions',
          children: regionOptions
        },{
          id: 'countries',
          text: 'Countries',
          children: countryOptions
        }],
        placeholder: '',
        scrollAfterSelect: true,
        renderTemplate: item => item.text || '\u200B',
        dropdownParent: $('#regionDrop'),
        shouldFocusInput: function() {
        return false;
      },
        templateResult: function(data) {
        var $state = $('<span>' + data.text + '<span>');
        return $state;
      }
      }).on('select2:unselecting', function() {
          $(this).data('unselecting', true);
      }).on('select2:opening', function(e) {
          if ($(this).data('unselecting')) {
              $(this).removeData('unselecting');
              e.preventDefault();
          }
      });

      $('#regionSelect').on('select2:select', function (e) {
          var data = e.params.data;
          filter([data.id]);
      });

      $('#regionSelect').on('select2:unselect', function (e) {
        var data = e.params.data;
        filter([data.id]);
    });

      // reset locations
      d3.select('#reset_locations').style('cursor', 'pointer').attr('opacity', 1)
      .on('click', function(){
        filter('clear');
      }); 

      // bubble map select color by
      $('#colorSelect').select2({
        scrollAfterSelect: true,
        minimumResultsForSearch: -1,
        renderTemplate: item => item.text || '\u200B',
        dropdownParent: $('#regionDrop'),
        shouldFocusInput: function() {
        return false;
      },
        templateResult: function(data) {
        var $state = $('<span>' + data.text + '<span>');
        return $state;
      }
      }).on('select2:select', function(e) {
        var d = e.params.data;
        options.bubble_chart_color_by = d.id;
        updateBubblechart(data, options, filter);
      });

      // bubble map select y-axis
      $('#ySelect').select2({
        scrollAfterSelect: true,
        minimumResultsForSearch: -1,
        renderTemplate: item => item.text || '\u200B',
        dropdownParent: $('#regionDrop'),
        shouldFocusInput: function() {
        return false;
      },
        templateResult: function(data) {
        var $state = $('<span>' + data.text + '<span>');
        return $state;
      }
      }).on('select2:select', function(e) {
        var d = e.params.data;
        options.bubble_chart_y_axis = d.id;
        updateBubblechart(data, options, filter);
      });

  }

  function handleSelectLength(){
    var values = $('#regionSelect').select2('data');
    // handle height for many selections
    var select2HeightRatio = $('#regionSelectContainer').width()/$('#regionSelectContainer').height();
    if(select2HeightRatio<10){
      $('.select2-selection__choice').hide();
      $('.select2-search__field').attr('placeholder', values.length+' LOCATIONS SELECTED' ).css('width', '100%')
    } else {
      $('.select2-search__field').attr('placeholder', '' );
      $('.select2-selection__choice').show();
    }
  }

  useEffect(() => {
    init();
  }, [])


  const selectStyle = {
    control: (base, state) => ({
      ...base,
      border: '1px solid #D8D8D8',
      boxShadow: 'none',
      '&:hover': {
          border: '1px solid #D8D8D8',
