import React, { Component, useEffect, useState } from "react";
import Select from 'react-select'
import * as d3 from "d3";
import axios from 'axios';
import Layout from './Layout';
import options from './Options';
import './App.css'
import $ from "jquery";

const equityDataUrl = './data/equity_data.csv';
const layoutUrl = "./images/layout.svg";
const whoDataUrl = "./data/who_global_cases.csv";
const worldUrl = "./data/world-wb5.geojson";

const data = {'data': []};

const regionOptions = [
  { value: 'AM', label: 'Americas' },
  { value: 'EU', label: 'Europe' },
  { value: 'AS', label: 'Asia-Pacific' },
  { value: 'ME', label: 'Middle East and North Africa'},
  { value: 'AF', label: 'Africa' }
]

function App() {

  const [progress, setProgress] = useState(0);

  async function init(){


    // data source 1: layout.svg
    const layout = d3.xml(layoutUrl);
    layout.then(function(d){
      d3.select('#svg_container').node().append(d3.select(d.documentElement).attr('width', '100%').attr('height', 'auto').node());
      const svg = d3.select('#svg_container svg');
      svg.select('title').remove();
      console.log('load layout');
      Layout(svg, options, data);
      setProgress(0.1);
      // data source 2: equity_data.csv
      const equityData = d3.csv(equityDataUrl);
      equityData.then(function(d){
        data.equity_data = d;
        setProgress(0.3);
        console.log(data);
        // d3.select('#loaderDiv').remove();
      });
    })
  }

  useEffect(() => {
    init();
  }, [])


  return (
    <div>
      <div id="loaderDiv">
        <div id="loaderBar">
          <div id="loaderProgress"></div>
        </div>
      </div>

      <div id="regionSelectContainer">
        <Select id="regionSelect" options={regionOptions} />
      </div>
      <div id="countrySelectContainer">
        <Select id="countrySelect" options={regionOptions} />
      </div>

    <div id="svg_container" className="App">

    </div>


    </div>
  );
}

export default App;
