import * as d3 from "d3";

export function drawTimechart(data, options) {

    console.log('draw timechart');
    console.log(data);
    console.log(options);

    // define date range
    const minDate = new Date(2020, 0, 1);
    const maxDate = new Date(d3.max(data.who_data, function(d){
        return d.Date_reported;
    }));

    const numDays = 10;

    const svg = d3.select('#timechart_svg').attr('fill', 'blue');

    const canvas = svg.append('canvas')
    .attr('width', 10)
    .attr('height', svg.attr('height'))
    .attr('y', 0)
    .attr('x', 0)
    .style('stroke', '1px solid green')
    .style('width', '100%')
    .style('height', '100%')

    console.log(minDate);
    console.log(maxDate);

}

export function updateTimechart(data) {

    console.log('update timechart');

}

export default drawTimechart;
