import * as d3 from "d3";
import moment from 'moment';

export function drawTimechart(data, options) {

    // console.log('draw timechart');
    // console.log(data);
    // console.log(options);
    // const groupedData = d3.group(data.who_data, d => d[options.timeline_frequency+'Total'], d => d.ifrc_region)

    const groupedData = [];

    data.who_data.forEach(function(d,i){
        groupedData[d[options.timeline_frequency+'Total']] = groupedData[d[options.timeline_frequency+'Total']] || {};
        if(options.timeline_frequency=='day') groupedData[d[options.timeline_frequency+'Total']].date = d.date;
        if(options.timeline_frequency=='week') groupedData[d[options.timeline_frequency+'Total']].date = moment(d.date).startOf('isoWeek').toDate();
        if(options.timeline_frequency=='month') groupedData[d[options.timeline_frequency+'Total']].date = moment(d.date).startOf('month').toDate();
        groupedData[d[options.timeline_frequency+'Total']][d.ifrc_region] = groupedData[d[options.timeline_frequency+'Total']][d.ifrc_region] || {new_cases: 0, new_deaths: 0, cumulative_cases: 0, cumulative_deaths: 0};
        groupedData[d[options.timeline_frequency+'Total']][d.ifrc_region].new_cases += d.new_cases;
        groupedData[d[options.timeline_frequency+'Total']][d.ifrc_region].new_deaths += d.new_deaths;
        groupedData[d[options.timeline_frequency+'Total']][d.ifrc_region].cumulative_cases += d.cumulative_cases;
        groupedData[d[options.timeline_frequency+'Total']][d.ifrc_region].cumulative_deaths += d.cumulative_deaths;
        groupedData[d[options.timeline_frequency+'Total']].total_new_cases = groupedData[d[options.timeline_frequency+'Total']].total_new_cases + d.new_cases;
        // groupedData[d[options.timeline_frequency+'Total']].total_cumulative_cases += d.cumulative_cases;

    })

    console.log(groupedData);

    const numIntervals = d3.max(data.who_data, function(d,i){
        return d[options.timeline_frequency+'Total'];
    });

    // const maxCases = d3.max(groupedData)

    // define date range
    const minDate = groupedData[0].date;
    const maxDate = groupedData[numIntervals].date;

    const svg = d3.select('#timechart_svg').attr('fill', 'blue');
    const width = parseInt(svg.attr('width'));
    const barWidth = width/numIntervals;
    const barSpacing = 0.2;

    const xScale = d3.scaleLinear()
    .range([0,width])
    .domain([minDate,maxDate]);

    const yScaleCases = d3.scaleLinear()
    .range([140,0])
    .domain([0,maxDate]);


    console.log('minDate');
    console.log(minDate);
    console.log('maxDate');
    console.log(maxDate);

    // CASES
    // loop through groupedData and draw bars
    const casesBarGroups = svg.selectAll('.cases_bar_group')
    .data(groupedData)
    .enter()
    .append('g')
    .attr('transform', function(d,i){ return 'translate('+xScale(d.date)+')'})

    casesBarGroups.append('rect')
    .attr('x', (barWidth*barSpacing))
    .attr('y', 0)
    .attr('width', barWidth-(barWidth*barSpacing*2))
    .attr('height', 100);

    // 10

    // 0.1

    // 1 - 8 - 1

    // draw x-axis


    // draw y-axis

}

export function updateTimechart(data) {

    console.log('update timechart');

}

export default drawTimechart;
