import React, { Component, useEffect } from "react";
import Select from 'react-select'
import * as d3 from "d3";
import axios from 'axios';
import Layout from './Layout';
import options from './Options';
import './App.css'
import $ from "jquery";

const equityDataUrl = './data/equity_data.csv';
const layoutUrl = "./images/layout.svg";
const whoDataUrl = "./data/who_global_cases.csv";
const worldUrl = "./data/world-wb5.geojson";

const data = {'data': []};

const regionOptions = [
  { value: 'AM', label: 'Americas' },
  { value: 'EU', label: 'Europe' },
  { value: 'AS', label: 'Asia-Pacific' },
  { value: 'ME', label: 'Middle East and North Africa'},
  { value: 'AF', label: 'Africa' }
]

function App() {

  async function init(){
    const layout = d3.xml(layoutUrl);

    // data source 1: equity data
    // let response = await fetch(equityDataUrl);
    // const reader = response.body.getReader();
    // const contentLength = +response.headers.get('Content-Length');
    // console.log(contentLength);

    axios.get(equityDataUrl, {
      onDownloadProgress: progressEvent => {
        const total = parseFloat(progressEvent.currentTarget.responseHeaders['Content-Length'])
        const current = progressEvent.currentTarget.response.length

        let percentCompleted = Math.floor(current / total * 100)
        console.log('completed: ', percentCompleted)
      }
    })
    .then(res => {
      console.log("All DONE: ", res.headers)
      return res.data
    })

    layout.then(function(d){
      console.log(layout);
      d3.select('#svg_container').node().append(d3.select(d.documentElement).attr('width', '100%').attr('height', 'auto').node());
      const svg = d3.select('#svg_container svg');
      svg.select('title').remove();
      console.log('load layout');
      Layout(svg, options, data);
      console.log(options);
      console.log(data);

    })

  }

  useEffect(() => {
    init();
  }, [])


  return (
    <div>
      <div id="regionSelectContainer">
        <Select id="regionSelect" options={regionOptions} />
      </div>
      <div id="countrySelectContainer">
        <Select id="countrySelect" options={regionOptions} />
      </div>

    <div id="svg_container" className="App">

    </div>


    </div>
  );
}

export default App;
