// default options
const options = {
    region_chart_toggle: 'cases',
    region_filter: [],
    country_filter: [],
    timeline_type: 'cumulative',
    timeline_frequency: 'weekly', // daily, weekly, monthly
    map_toggle: 'bi-weekly-change' // bi-weekly-change, cases, deaths, percent-fully-vaccinated, percent-vaccine-acceptance
}

export default options;
