// import * as d3 from "d3";
import {min,max,group,flatGroup} from "d3";

import moment from 'moment';

export function ParseData(data, options) {

    console.log('ParseData');
    console.log(options);

    // max dates
    let maxDate = moment(new Date(max(data.who_data, function(d){
        return d.Date_reported;
    })));

    let minDateVaccines = (new Date(min(data.vaccines_data, function(d){
        return d.date;
    })));


    let maxDateVaccines = (new Date(max(data.vaccines_data, function(d){
        return d.date;
    })));

    if(maxDateVaccines>maxDate) {
        maxDate = maxDateVaccines;
    }

    options.maxDate = maxDate;
    // options.minDate = minDate;

    // parse who data

    let dayTotal = -1;
    let dayCheck = 0;

    let weekTotal = -1;
    let weekCheck = -1;

    let monthTotal = 0;
    let monthCheck = 0;

    data.who_data.sort(function(a,b){
        return new Date(a.Date_reported) - new Date(b.Date_reported);
    });

    data.who_data.forEach(function(d,i){

        d.date = new Date(d.Date_reported);
        d.dayOfYear =  moment(d.date).dayOfYear();
        if(d.dayOfYear!==dayCheck) dayTotal++;
        d.dayTotal = dayTotal;

        d.week = moment(d.date).isoWeek();
        if(d.week!==weekCheck) weekTotal++;
        d.weekTotal = weekTotal;
        d.month = d.date.getMonth();
        if(d.month!==monthCheck) monthTotal++;
        d.monthTotal = monthTotal;
        d.year = d.date.getFullYear();
        d.new_cases = parseInt(d.New_cases);
        d.new_deaths = parseInt(d.New_deaths);
        d.cumulative_deaths = parseInt(d.Cumulative_deaths);
        d.cumulative_cases = parseInt(d.Cumulative_cases);
        d.country_code = d.Country_code;
        d.country_name = null;
        data.equity_data.forEach(function(dd,ii){
            if(dd.country_iso2===d.Country_code){
                d.ifrc_region = dd.ifrc_region;
                d.country_name = dd.country_name;
            }
        })
        if(d.ifrc_region==undefined) d.ifrc_region = 'OTH';
        delete d.Date_reported;
        delete d.Country;
        delete d.New_cases;
        delete d.New_deaths;
        delete d.Country_code;
        delete d.Cumulative_deaths;
        delete d.Cumulative_cases;
        // delete d.Country_code;
        delete d.WHO_region;
        // delete d.Country;
        dayCheck = d.dayOfYear;
        weekCheck = d.week;
        monthCheck = d.month;
    })


    // parse vaccine data

    dayTotal = -1;
    dayCheck = 0;

    weekTotal = -1;
    weekCheck = -1;
    monthTotal = 0;
    monthCheck = 0;

    // filter out vaccine data on only include records with ISO3 country codes
    data.vaccines_data = data.vaccines_data.filter(function(d,i){
        return d.iso_code.length===3;
    })

    // fill out missing vaccinations date records (extend cumulative)
    var vGroup = flatGroup(data.vaccines_data, d => d.iso_code);

    console.log('minDateVaccines');
    console.log(minDateVaccines);
    console.log('maxDateVaccines');
    console.log(maxDateVaccines);


    vGroup.forEach(function(d,i){
        if((i<3)||(d[0] =='BFA')) console.log(d);

        if((i<3)||(d[0]=='BFA')){

            var newDates = [];
            var initDate = 0;

            // for (let day = (minDateVaccines);day <= (maxDateVaccines); day.setDate(day.getDate() + 1)) {
            //     var init = false;

            //     for(var i = 0; i < d[1].length; i++) {

            //     var dt = new Date(d[1][i].date);
            //     // if(.includes)

            //     if((day.getTime())==(dt.getTime())) {
            //         initDate=1;
            //         console.log('init at:');
            //         // console.log(day);
            //     }
            //     if(initDate!=1){
            //         newDates.push({iso_code: d[0], date: new Date(day), daily_vaccinations: 0, total_vaccinations: 0})
            //     } else {

            //     // }
            //     // // console.log(day)
            //     }

            // }

            var hasDays = [];
            d[1].forEach(function(dd,ii){
                hasDays.push(new Date(dd.date).getTime());
            })

            d[1].forEach(function(dd,ii){
                var dt = new Date(dd.date).getTime();
                // console.log('dt');
                // console.log(dt);

                for (let day = minDateVaccines;day <= maxDateVaccines; day.setDate(day.getDate() + 1)) {
                    if((day.getTime())==(dt)) {
                        initDate=1;
                        // console.log('init at:');
                        // console.log(day);
                    }
                    if(initDate!=1){
                        newDates.push({iso_code: d[0], date: new Date(day), daily_vaccinations: 0, total_vaccinations: 0})
                    } else {

                    }
                    // console.log(day)

                }

            });

            d[1].push(...newDates);

            d[1].sort(function(a,b){
                return new Date(a.date) - new Date(b.date);
            });

            initDate = 0;

        }


    })

    console.log('vGroup');
    console.log(vGroup);
    console.log(vGroup[31]);

    data.vaccines_data.sort(function(a,b){
        return new Date(a.date) - new Date(b.date);
    });

    data.vaccines_data.forEach(function(d,i){

        d.date = new Date(d.date);
        d.dayOfYear =  moment(d.date).dayOfYear();
        if(d.dayOfYear!==dayCheck) dayTotal++;
        d.dayTotal = dayTotal;

        d.week = moment(d.date).isoWeek();
        if(d.week!==weekCheck) weekTotal++;
        d.weekTotal = weekTotal;
        d.month = d.date.getMonth();
        if(d.month!==monthCheck) monthTotal++;
        d.monthTotal = monthTotal;
        d.year = d.date.getFullYear();
        if(d.daily_vaccinations){
            d.new_vaccines = parseInt(d.daily_vaccinations);
        } else {
            d.new_vaccines = 0;
        }
        if(d.total_vaccinations){
            d.cumulative_vaccines = parseInt(d.total_vaccinations);
        } else {
            d.cumulative_vaccines = 0;
        }
        d.country_code = null;
        d.country_name = null;
        data.equity_data.forEach(function(dd,ii){
            if(dd.country_iso3===d.iso_code){
                d.ifrc_region = dd.ifrc_region;
                d.country_name = dd.country_name;
                d.country_code = dd.country_iso2;
            }
        })
        if(d.ifrc_region==undefined) d.ifrc_region = 'OTH';
        delete d.Date_reported;
        // delete d.New_cases;
        // delete d.New_deaths;
        // delete d.location;
        // delete d.iso_code;
        dayCheck = d.dayOfYear;
        weekCheck = d.week;
        monthCheck = d.month;
    })

    console.log(data);
}

export default ParseData;
