      d3.select('#col4bar' + i).attr('opacity', 1).transition().duration(200).attr('width', function () {
        var val = d[1][0].vaccine_acceptance_percent;

        if (d[1][0].vaccine_acceptance_percent > 100) {
          val = 100;
        }

        if (isNaN(val)) {
          return 0;
        } else {
          return 68 * (val / 100);
        }
      });
      d3.select('#col4bg' + i).attr('opacity', 1);
      var dayFormat = d3.timeFormat("%b %d"); // col 5 val

      d3.select('#vac-tab').append('text').attr('class', 'tablerow').text(function () {
