      d3.select('#col1bg'+i)
      .attr('opacity', 1);

      // col 2 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = parseFloat(d[1][0].people_vaccinated_per_hundred).toFixed(1);
        if((isNaN(val))||(val==0)){ return ''} else { return val+'%' }
      })
      .style('text-anchor', 'end')
      .attr('x', 223)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 2 bar
      d3.select('#col2bar'+i)
      .attr('opacity', 1)
      .transition()
      .duration(200)
      .attr('width', function(){
        var val = d[1][0].people_vaccinated_per_hundred;
        if(d[1][0].people_vaccinated_per_hundred>100){
          val = 100
        } 
        if(isNaN(val)){ return 0} else { return 68*(val/100) }
      });

      d3.select('#col2bg'+i)
      .attr('opacity', 1);

      // col 3 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = parseFloat(d[1][0].people_fully_vaccinated_per_hundred).toFixed(1);
        if((isNaN(val))||(val==0)){ return ''} else { return val+'%' }
      })
      .style('text-anchor', 'end')
      .attr('x', 335)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 3 bar
      d3.select('#col3bar'+i)
      .attr('opacity', 1)
      .transition()
      .duration(200)
      .attr('width', function(){
        var val = d[1][0].people_fully_vaccinated_per_hundred;
        if(d[1][0].people_fully_vaccinated_per_hundred>100){
          val = 100
        } 
        if(isNaN(val)){ return 0} else { return 68*(val/100) }
      });

      d3.select('#col3bg'+i)
      .attr('opacity', 1);

      // col 4 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = parseFloat(d[1][0].vaccine_acceptance_percent).toFixed(1);
        if((isNaN(val))||(val==0)){ return ''} else { return val+'%' }
      })
      .style('text-anchor', 'end')
      .attr('x', 450)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 4 bar
      d3.select('#col4bar'+i)
      .attr('opacity', 1)
      .transition()
      .duration(200)
      .attr('width', function(){
        var val = d[1][0].vaccine_acceptance_percent;
        if(d[1][0].vaccine_acceptance_percent>100){
          val = 100
        } 
        if(isNaN(val)){ return 0} else { return 68*(val/100) }
      });

      d3.select('#col4bg'+i)
      .attr('opacity', 1);


      var dayFormat = d3.timeFormat("%b %d");
      // col 5 val
      d3.select('#vac-tab').append('text')
      .attr('class','tablerow')
      .text(function(){
        var val = (d[1][0].total_vaccinations);
        if((isNaN(val))||(val==0)){ return ''} else { return nFormatter(val) }
      })
      .style('text-anchor', 'end')
      .attr('x', 572)
      .attr('y', function(){
        return 80+(i*24.8);
      })
      .style('fill', '#000')
      .style('font-size', '9px');

      // col 6 val
      d3.select('#vac-tab').append('text')
