import * as d3 from "d3";
import moment from 'moment';
import colors from './colors';
import {addCommas} from './HelperFunctions';
import {filter} from './App';

let maxX =1000;
let maxY = 100;
let maxZ = 1000000;
let xScale; 
let yScale; 
let zScale; 
let groupedData = [];
let width = 600;
let height = 233;
let x = 40;
let y = 58;
let bubbles;

export function drawBubblechart(data, options, filter) {

    data.vGroup.forEach(function(d,i){
        data.equity_data.forEach(function(dd,ii){
            if(d[0] == dd.country_iso3){
                dd.people_fully_vaccinated_per_hundred = parseFloat(d[1][0].people_fully_vaccinated_per_hundred);
                dd.people_vaccinated_per_hundred = parseFloat(d[1][0].people_vaccinated_per_hundred);
                dd.total_vaccinations = parseFloat(d[1][0].total_vaccinations);
                dd.total_vaccinations_per_hundred = parseFloat(d[1][0].total_vaccinations_per_hundred);

            }
        })
    })
    console.log(data);

    maxX = d3.max(data.equity_data, function(d,i){
        return parseFloat(d.gni_2017);
    })

    maxY = d3.max(data.equity_data, function(d,i){
        if(options.bubble_chart_y_axis == 'percent-vaccinated-at-least-1-dose') return parseFloat(d.people_vaccinated_per_hundred);
        if(options.bubble_chart_y_axis == 'percent-fully-vaccinated') return parseFloat(d.people_fully_vaccinated_per_hundred);
        if(options.bubble_chart_y_axis == 'percent-vaccine-acceptance') return parseFloat(d.vaccince_acceptance_percent);
    });

    maxZ = d3.max(data.equity_data, function(d,i){
        return parseFloat(d.population);
    });

    xScale = d3.scaleLinear()
	.range([0, width])
	.domain([0, maxX]);

    yScale = d3.scaleLinear()
	.range([height, 0])
	.domain([0, maxY]);

    zScale = d3.scaleSqrt()
    .domain([0, maxZ])
    .range([1, 20]);

    const svg = d3.select('#bubblechart-tab').append('g').attr('transform', 'translate('+x+', '+y+')')
    .attr('id', 'bubblechart');

    // create y-axis
    svg.append("g")
    .attr('id', 'bubbley')
    .call(d3.axisLeft(yScale).ticks(3));

    // create x-axis
    svg.append("g")
    .attr('id', 'bubblex')
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(xScale).ticks(3));

    // Add dots
    bubbles = svg.append('g')
    .selectAll(".bubble")
    .data(data.equity_data)
    .join(function(enter){
        enter.append("circle")
        .attr("class", function(d) { return "bubble" })
        .attr("cx", function (d) { return xScale(parseFloat(d.gni_2017)); } )
        .attr("cy", function (d) { 
            if(options.bubble_chart_y_axis == 'percent-vaccinated-at-least-1-dose') return yScale(parseFloat(d.people_vaccinated_per_hundred));
            if(options.bubble_chart_y_axis == 'percent-fully-vaccinated') return yScale(parseFloat(d.people_fully_vaccinated_per_hundred));
            if(options.bubble_chart_y_axis == 'percent-vaccine-acceptance') return yScale(parseFloat(d.vaccince_acceptance_percent));
        })
        .attr("r", function (d) { return zScale(parseFloat(d.population)); } )
        .style("fill", function (d) { 
            if(options.bubble_chart_color_by == 'region') {
               if(colors.regions[d.ifrc_region]) return colors.regions[d.ifrc_region];
            } 
            if(options.bubble_chart_color_by == 'hdi') {
                if(colors.hdi_category[d.hdi_category]) return colors.hdi_category[d.hdi_category];
             }
             if(options.bubble_chart_color_by == 'inform-severity') {
                if(colors.inform_severity[d.inform_severity]) return colors.inform_severity[d.inform_severity];
             }
            return  'transparent';
        })
        .style("stroke", function (d) { 
            if(options.bubble_chart_color_by == 'region') {
               if(colors.regions[d.ifrc_region]) return colors.regions[d.ifrc_region];
            } 
            if(options.bubble_chart_color_by == 'hdi') {
                if(colors.hdi_category[d.hdi_category]) return colors.hdi_category[d.hdi_category];
             }
             if(options.bubble_chart_color_by == 'inform-severity') {
                if(colors.inform_severity[d.inform_severity]) return colors.inform_severity[d.inform_severity];
             }
            return  'transparent';
        })
        .style("stroke-width", 2)
        .style("fill-opacity", 0.8);
    }, function(update){
        update.attr("cx", function (d) { return xScale(parseFloat(d.gni_2017)); } )
        .attr("cy", function (d) { 
            if(options.bubble_chart_y_axis == 'percent-vaccinated-at-least-1-dose') return yScale(parseFloat(d.people_vaccinated_per_hundred));
            if(options.bubble_chart_y_axis == 'percent-fully-vaccinated') return yScale(parseFloat(d.people_fully_vaccinated_per_hundred));
            if(options.bubble_chart_y_axis == 'percent-vaccine-acceptance') return yScale(parseFloat(d.vaccince_acceptance_percent));
        })
        .attr("r", function (d) { return zScale(parseFloat(d.population)); } )
        .style("fill", function (d) { 
            if(options.bubble_chart_color_by == 'region') {
               if(colors.regions[d.ifrc_region]) return colors.regions[d.ifrc_region];
            } 
            if(options.bubble_chart_color_by == 'hdi') {
                if(colors.hdi_category[d.hdi_category]) return colors.hdi_category[d.hdi_category];
             }
             if(options.bubble_chart_color_by == 'inform-severity') {
                if(colors.inform_severity[d.inform_severity]) return colors.inform_severity[d.inform_severity];
             }
            return  'transparent';
        })
        .style("stroke", function (d) { 
            if(options.bubble_chart_color_by == 'region') {
               if(colors.regions[d.ifrc_region]) return colors.regions[d.ifrc_region];
            } 
            if(options.bubble_chart_color_by == 'hdi') {
                if(colors.hdi_category[d.hdi_category]) return colors.hdi_category[d.hdi_category];
             }
             if(options.bubble_chart_color_by == 'inform-severity') {
                if(colors.inform_severity[d.inform_severity]) return colors.inform_severity[d.inform_severity];
             }
            return  'transparent';
        })
    },function(exit){

    })



}

