import React, { useEffect, useState } from "react";
import Select from 'react-select'
import * as d3 from "d3";
import Layout from './Layout';
import {drawTimechart, updateTimechart} from './Timechart';
import {drawRegionchart, updateRegionchart} from './Regionchart';
import options from './Options';
import ParseData from './ParseData';
import './App.css'
// import $ from "jquery";

const equityDataUrl = './data/equity_data.csv';
const layoutUrl = "./images/layout.svg";
const whoDataUrl = "./data/who_global_cases.csv";
const worldUrl = "./data/world-wb5.geojson";

const data = {'data': []};

const regionOptions = [
  { value: 'AM', label: 'Americas' },
  { value: 'EU', label: 'Europe' },
  { value: 'AP', label: 'Asia-Pacific' },
  { value: 'ME', label: 'Middle East and North Africa'},
  { value: 'AF', label: 'Africa' }
]

const countryOptions = [];

function App() {

  const [progress, setProgress] = useState(0);
  const [loaderText, setLoaderText] = useState('Loading SVG layout...');
  // const [countryOptions, setCountryOptions] = useState([]);

  async function init(){

    // data source 1: layout.svg
    const layout = d3.xml(layoutUrl);
    layout.then(function(d){
      d3.select('#svg_container').node().append(d3.select(d.documentElement).attr('width', '100%').attr('height', 'auto').node());
      const svg = d3.select('#svg_container svg');
      svg.select('title').remove();
      Layout(svg, options, data);
      setProgress(0.1);
      setLoaderText('Loading equity data CSV...');
      // data source 2: equity_data.csv
      const equityData = d3.csv(equityDataUrl);
      equityData.then(function(d){
        data.equity_data = d;

        d.forEach(function(d,i){
          console.log(d);
          countryOptions.push({'label': d.country_name, 'value': d.country_iso2 })
        })
        console.log(countryOptions);

        setProgress(0.3);
        setLoaderText('Loading WHO data CSV...');
          // data source 3: who_global_cases.csv
          const whoData = d3.csv(whoDataUrl);
          whoData.then(function(d){
            data.who_data = d;
            setProgress(0.8);
            setLoaderText('Loading World Map GeoJSON...');
            // data source 4: world-wb5.geojson
            const world = d3.csv(worldUrl);
            world.then(function(d){
              data.world = d;
              setLoaderText('Loading complete.');
              setProgress(1);
              ParseData(data);
              createCharts();
              console.log(data);
              d3.select('#loaderDiv').transition().duration(1000).style('opacity', 0).on("end", () => d3.select('#loaderDiv').remove());
            });
          });
      });
    })
  }

  function createCharts(){
    drawTimechart(data, options);
    drawRegionchart(data, options);
  }

  useEffect(() => {
    init();
  }, [])


  const selectStyle = {
    control: base => ({
      ...base,
      border: 0,
      // boxShadow: 'none'
    })
  };

  return (
    <div>
      <div id="loaderDiv">
        <div id="loaderText">{loaderText}</div>
        <div id="loaderBar">
          <div id="loaderProgress" style={{width: progress*100+'%'}}></div>
        </div>
      </div>

      <div id="regionSelectContainer">
        <Select id="regionSelect" options={regionOptions} styles={selectStyle}/>
      </div>
      <div id="countrySelectContainer">
        <Select id="countrySelect" options={countryOptions} styles={selectStyle}/>
      </div>

    <div id="svg_container" className="App">

    </div>


    </div>
  );
}

export default App;
