                if(d[1].percent_change>1){ return '#E02225'} else { return '#1F558C'}
            })
        })

    }

    if(options.map_toggle == 'cases'){
        var maxValue = d3.max(data.country, function(d,i){
            return d[1].cumulative_cases;
        })

        var rScale = d3.scalePow().exponent(0.5)
        .domain([0, maxValue])
