import React, { useEffect } from "react";
import './App.css';
import * as d3 from "d3";
import * as loadLayout from '/loadLayout.js';

function App() {

  async function mainFunc(){
    const layout = d3.xml("./images/layout.svg");
    layout.then(function(data){
      console.log(layout);
      d3.select('#container').node().append(d3.select(data.documentElement).attr('width', '100%').attr('height', 'auto').node());
      const svg = d3.select('#container svg');
      // svg.select('#global-cases-1').attr('opacity', 0.5)
    })

    console.log('hello');
    const data = await d3.csv("./images/layout.svg");
    console.log(data);
  }

  useEffect(() => {
    // code to run on component mount
    mainFunc();

  }, [])




  return (
    <div id="container" className="App">

    </div>
  );
}

export default App;
