// default options
const options = {
    region_chart_toggle: 'cases', // cases, deaths, vaccines
    region_filter: [],
    country_filter: [],
    hdi_filter: [],
    inform_severity_filter: [],
    timeline_type: 'cumulative', // cumulative, non-cumulative, stacked 
    timeline_frequency: 'weekly', // daily, weekly, monthly
    map_toggle: 'bi-weekly-change' // bi-weekly-change, cases, deaths, percent-fully-vaccinated, percent-vaccine-acceptance
}

export default options;
