import React, { useEffect } from "react";
import * as d3 from "d3";
import Layout from './Layout';
import options from './Options';
import './App.css'
import { Select2 } from "select2-react-component";

const data = {'data': []};

const regionOptions = [
  { value: 'Americas', label: 'Americas' },
  { value: 'Europe', label: 'Europe' },
  { value: 'Asia', label: 'Asia-Pacific' },
  { value: 'Middle East and North Africa', label: 'Middle East and North Africa'},
  { value: 'Africa', label: 'Africa' }
]

function App() {

  async function init(){
    const layout = d3.xml("./images/layout.svg");
    layout.then(function(d){
      console.log(layout);
      d3.select('#svg_container').node().append(d3.select(d.documentElement).attr('width', '100%').attr('height', 'auto').node());
      const svg = d3.select('#svg_container svg');
      console.log('load layout');
      Layout(svg, options, data);
      console.log(options);
      console.log(data);
    })

  }

  function createRegionSelect(){
      let items = [];         
      regionOptions.forEach(function(d,i){
        items.push(<option key={d} value={d}>{d}</option>);   
      })         
      return items;
  } 

  useEffect(() => {
    init();
  }, [])


  return (
    <div>

    <div id="svg_container" className="App">

    </div>

    <select id="regionSelect" name="region[]" multiple="multiple">
     { createRegionSelect() }
    </select>

    </div>
  );
}

export default App;


